import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import SearchBar from '../components/SearchBar';
import { io } from 'socket.io-client';


const Home = () => {

    const socket = io('http://localhost:4444'); // Adjust the server address

    useEffect(() => {
      socket.on('searchResult', (result) => {
        console.log('Received search result:', result);
        // Handle the search result as needed
      });

      return () => {
        socket.disconnect();
      };
    }, []);

    const handleSearch = (searchTerm) => {
        const socket = io('http://127.0.0.1:4444'); // Replace with your backend IP and port
        socket.emit('search', { searchTerm, type: 'SEARCH' });
        console.log(`Searching for: ${searchTerm}`);

        // You can also send this searchTerm to your backend, etc.
      };


    return (
      <View style={styles.container}>
        <View style={styles.content}>
          <SearchBar onSearch={handleSearch}/>
        </View>
      </View>
    );
  };

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    content: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
    },
    searchContainer: {
      alignItems: 'center',
      padding: 16,
    },
});

export default Home;
